// Calculate 7-day time window with validation and logging
const now = $now;
const sevenDaysAgo = now.minus({ days: 7 });
const sevenDaysAgoTs = Math.floor(sevenDaysAgo.toMillis() / 1000);

// Validate timestamp
if (sevenDaysAgoTs <= 0) {
  throw new Error('Invalid timestamp calculation');
}

// Log execution start
console.log('Workflow execution started:', {
  executionId: $execution.id,
  workflowId: $workflow.id,
  timestamp: now.toISOString(),
  sevenDaysAgoTs,
  timeWindow: '7 days'
});

return [{ 
  json: { 
    sevenDaysAgoTs,
    executionStart: now.toISOString(),
    workflowId: $workflow.id,
    executionId: $execution.id
  } 
}];

// Enhanced message processing with validation, error handling, and comprehensive logging
const messages = $input.all();
const relevantMessages = [];
let processedCount = 0;
let skippedCount = 0;
let hubspotCount = 0;
let errorCount = 0;

// Input validation with detailed logging
console.log('Starting message processing:', {
  totalMessages: messages.length,
  executionId: $execution.id,
  timestamp: $now.toISOString()
});

if (!Array.isArray(messages) || messages.length === 0) {
  console.log('No messages found or invalid input format');
  return [{ 
    json: { 
      slackLog: '', 
      processedCount: 0, 
      skippedCount: 0, 
      hubspotCount: 0, 
      errorCount: 0,
      totalMessages: 0,
      processingStatus: 'NO_MESSAGES'
    } 
  }];
}

// Process each message with comprehensive error handling
for (const item of messages) {
  try {
    const message = item.json;

    // Validate message structure
    if (!message || typeof message !== 'object') {
      skippedCount++;
      continue;
    }

    // Skip system messages and bots
    if (message.subtype === 'channel_join' || 
        message.subtype === 'channel_leave' || 
        message.subtype === 'bot_message' ||
        message.subtype === 'channel_topic' ||
        message.subtype === 'channel_purpose') {
      skippedCount++;
      continue;
    }

    let formattedMessage = '';
    const messageDate = new Date(message.ts * 1000).toLocaleDateString('de-DE');

    // Enhanced HubSpot meeting notes detection
    if (message.user_profile?.name === 'HubSpot' || 
        message.username === 'HubSpot' || 
        message.user === 'HubSpot') {
      const text = message.attachments?.[0]?.text || message.text || '';
      if (text.includes('Zusammenfassung:') || 
          text.includes('N√§chste Schritte:') || 
          text.includes('Meeting Summary') ||
          text.includes('Summary:')) {
        formattedMessage = `\n=== MEETING SUMMARY on ${messageDate} ===\n${text}\n=== END SUMMARY ===\n`;
        hubspotCount++;
      }
    } else if (message.text && message.text.trim()) {
      // Process regular user messages with enhanced sanitization
      const userName = message.user_profile?.display_name || 
                       message.user_profile?.real_name || 
                       message.user || 'Unknown User';

      // Enhanced XSS prevention and content sanitization
      const sanitizedText = message.text
        .replace(/[<>]/g, '')
        .replace(/&/g, '&amp;')
        .replace(/"/g, '&quot;')
        .replace(/'/g, '&#x27;');

      formattedMessage = `[${userName}] on [${messageDate}]: ${sanitizedText}`;
    }

    if (formattedMessage) {
      relevantMessages.push(formattedMessage);
      processedCount++;
    } else {
      skippedCount++;
    }
  } catch (error) {
    errorCount++;
    console.error('Error processing message:', {
      error: error.message,
      messageId: item.json?.ts || 'unknown',
      executionId: $execution.id
    });
  }
}

// Combine messages with comprehensive processing stats
const consolidatedLog = relevantMessages.join('\n---\n');

// Log processing results
console.log('Message processing completed:', {
  processedCount,
  skippedCount,
  hubspotCount,
  errorCount,
  totalMessages: messages.length,
  logLength: consolidatedLog.length,
  executionId: $execution.id
});

return [{ 
  json: { 
    slackLog: consolidatedLog,
    processedCount,
    skippedCount,
    hubspotCount,
    errorCount,
    totalMessages: messages.length,
    processingStatus: 'COMPLETED'
  } 
}];

// Enhanced AI output validation with comprehensive error handling and retry logic
const aiResponse = $input.item.json.output || $input.item.json.response || $input.item.json.text || '';

// Log AI response for debugging
console.log('AI Response received:', {
  responseLength: aiResponse.length,
  responsePreview: aiResponse.substring(0, 200),
  executionId: $execution.id,
  timestamp: $now.toISOString()
});

// Input validation with detailed error reporting
if (!aiResponse || typeof aiResponse !== 'string') {
  const errorMsg = `Invalid AI response type: ${typeof aiResponse}. Expected string. Response: ${JSON.stringify(aiResponse).substring(0, 200)}`;
  console.error('AI Response validation failed:', errorMsg);
  throw new Error(errorMsg);
}

if (aiResponse.trim().length === 0) {
  const errorMsg = 'AI response is empty or contains only whitespace';
  console.error('AI Response validation failed:', errorMsg);
  throw new Error(errorMsg);
}

// Check for common AI error patterns
if (aiResponse.toLowerCase().includes('error') || 
    aiResponse.toLowerCase().includes('sorry') || 
    aiResponse.toLowerCase().includes('cannot') ||
    aiResponse.toLowerCase().includes('unable')) {
  console.warn('AI response contains potential error indicators:', aiResponse.substring(0, 200));
}

try {
  // Attempt to parse the AI's response
  let parsedJson;

  // Try to extract JSON if it's wrapped in markdown or other text
  const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    parsedJson = JSON.parse(jsonMatch[0]);
  } else {
    parsedJson = JSON.parse(aiResponse);
  }

  // Validate required fields with detailed error reporting
  const requiredFields = ['subject', 'greeting', 'summary', 'completed_tasks', 'next_steps', 'closing'];
  const missingFields = requiredFields.filter(field => !parsedJson.hasOwnProperty(field));

  if (missingFields.length > 0) {
    const errorMsg = `Missing required fields: ${missingFields.join(', ')}. Available fields: ${Object.keys(parsedJson).join(', ')}`;
    console.error('AI Response validation failed:', errorMsg);
    throw new Error(errorMsg);
  }

  // Validate field types and content
  if (typeof parsedJson.subject !== 'string' || parsedJson.subject.trim().length === 0) {
    throw new Error('Subject field is missing, empty, or not a string');
  }

  if (!Array.isArray(parsedJson.completed_tasks) || !Array.isArray(parsedJson.next_steps)) {
    throw new Error('completed_tasks and next_steps must be arrays');
  }

  if (parsedJson.completed_tasks.length === 0 && parsedJson.next_steps.length === 0) {
    console.warn('Both completed_tasks and next_steps arrays are empty');
  }

  // Enhanced content sanitization to prevent XSS and ensure quality
  const sanitizedJson = {
    subject: parsedJson.subject.replace(/[<>]/g, '').trim(),
    greeting: parsedJson.greeting.replace(/[<>]/g, '').trim(),
    summary: parsedJson.summary.replace(/[<>]/g, '').trim(),
    completed_tasks: parsedJson.completed_tasks.map(task => 
      task.replace(/[<>]/g, '').trim()
    ).filter(task => task.length > 0),
    next_steps: parsedJson.next_steps.map(step => 
      step.replace(/[<>]/g, '').trim()
    ).filter(step => step.length > 0),
    closing: parsedJson.closing.replace(/[<>]/g, '').trim()
  };

  // Validate content quality
  if (sanitizedJson.subject.length > 100) {
    console.warn('Subject is longer than recommended (100 chars):', sanitizedJson.subject.length);
  }

  if (sanitizedJson.summary.length < 50) {
    console.warn('Summary seems too short:', sanitizedJson.summary.length);
  }

  // Log successful validation
  console.log('AI Response validation successful:', {
    subjectLength: sanitizedJson.subject.length,
    completedTasksCount: sanitizedJson.completed_tasks.length,
    nextStepsCount: sanitizedJson.next_steps.length,
    executionId: $execution.id
  });

  return [{ json: sanitizedJson }];
} catch (error) {
  // Enhanced error reporting with context
  const errorMessage = error instanceof SyntaxError 
    ? `AI returned invalid JSON syntax. Response preview: ${aiResponse.substring(0, 300)}...`
    : `AI response validation failed: ${error.message}. Response preview: ${aiResponse.substring(0, 300)}...`;

  console.error('AI Response processing failed:', {
    error: errorMessage,
    executionId: $execution.id,
    timestamp: $now.toISOString()
  });

  throw new Error(errorMessage);
}

